command.text:
&CheckLockedBeforeUpdate;
if not exists(select * from @@table where stt_rec = $stt_rec.OldValue and ngay = $ngay.OldValue and loai = $loai.OldValue) begin select 'ma_nv' as field, @$recordHasBeenChanged as message return end
if (@stt_rec <> $stt_rec.OldValue or @ngay <> $ngay.OldValue or @loai <> $loai.OldValue) begin if exists(select * from @@table where (not (stt_rec = $stt_rec.OldValue and ngay = $ngay.OldValue and loai = $loai.OldValue)) and (stt_rec = @stt_rec and ngay = @ngay and loai = @loai)) begin select 'ma_nv' as field, @$exists as message return end end
