&OutlineEntry; function load$AutoGenerateStocktakingCreate$(a){var b=a.grid,d=a._language=="v",c=a.getItem("status");a.add_onResponseComplete(on$AutoGenerateStocktakingCreate$ResponseComplete);a._looking=a.getItem("ma_quyen")._controlBehavior;a._looking.add_loading(on$AutoGenerateStocktakingCreate$Before$Loading);a.setItemValues("stt_rec, ma_dvcs, ma_kho, type, ma_ct",[b._$k,b._$u,b._$s,b._$t,b._$t=="1"?"PXA":"PND"]);c.options[1].text=d?b._$t=="1"?"1. Xuất kho":"1. Nhập kho":b._$t=="1"?"1. Issue":"1. Receipt";a.request("StocktakingCreate","StocktakingCreate",["stt_rec","ma_dvcs","type"]);a.getItem("ngay_ct").focus();a.setReferenceKeyFilter("ma_quyen")}function dispose$AutoGenerateStocktakingCreate$(a){try{a._looking.remove_loading(on$AutoGenerateStocktakingCreate$Before$Loading)}catch(b){}try{a.remove_onResponseComplete(on$AutoGenerateStocktakingCreate$ResponseComplete)}catch(b){}}function on$AutoGenerateStocktakingCreate$ResponseComplete(e,c){var b=c.object,d=c.type.Context,a=c.type.Result;switch(d){case"StocktakingCreate":b.setItemValues("ngay_ct, ngay",[a[0].Value,a[0].Value]);if(a[1]){b.setItemValue("dien_giai",a[3].Value);b.setItemControlBehavior("ma_kh",a[1].Value,a[2].Value,true);b.setItemControlBehavior("tk",a[4].Value,a[5].Value,true);b.setItemControlBehavior("ma_nx",a[6].Value,a[7].Value,true)}}}function on$AutoGenerateStocktakingCreate$Before$Loading(a,b){var c=a._element.parentForm;if(!c.validFields("ngay_ct"))b.type.cancelEvent=true}function on$AutoGenerateStocktakingCreate$QueryComplete(a){var b=a.grid;b._formScript="show$AutoGenerateStocktakingCreate$QueryComplete(this)"}function show$AutoGenerateStocktakingCreate$QueryComplete(a){var d=a._language=="v",b=a.get_id(),c=d?"Chương trình đã thực hiện xong.":"The process was completed successfully.";$message.show(c,String.format("$find('{0}')._focusWhenTabChanged();",b));a._focusWhenTabChanged()}
declare @d smalldatetime select @d = ngay_ct from kkyc where stt_rec = @stt_rec if exists(select 1 from dmclkktd where ma_dvcs = @ma_dvcs) begin if @type = '1' select @d as ngay_ct, rtrim(a.ma_kh_thieu) as ma_kh_thieu, b.ten_kh%l, a.dien_giai_thieu, rtrim(a.tk_kk_thieu) as tk_kk_thieu, c.ten_tk%l, rtrim(a.ma_nx_thieu) as ma_nx_thieu, d.ten_nx%l from dmclkktd a left join dmkh b on a.ma_kh_thieu = b.ma_kh left join dmtk c on a.tk_kk_thieu = c.tk left join dmnx d on a.ma_nx_thieu = d.ma_nx where ma_dvcs = @ma_dvcs else select @d as ngay_ct, rtrim(a.ma_kh_thua) as ma_kh_thua, b.ten_kh%l, a.dien_giai_thua, rtrim(a.tk_kk_thua) as tk_kk_thua, c.ten_tk%l, rtrim(a.ma_nx_thua) as ma_nx_thua, d.ten_nx%l from dmclkktd a left join dmkh b on a.ma_kh_thua = b.ma_kh left join dmtk c on a.tk_kk_thua = c.tk left join dmnx d on a.ma_nx_thua = d.ma_nx where ma_dvcs = @ma_dvcs return end select @d as ngay_ct return
