declare @q nvarchar(4000), @key nvarchar(4000) select @key = 'ma_kh in (''' + replace(replace(replace(@ma_kh, ' ', ''), '''', ''), ',', ''',''') + ''')', @ma_dvcs = replace(@ma_dvcs, '''', '') select @key = @key + ' and e_mail <> '''''
if len(@key) > 3000 return
select top 0 ma_kh into #t from dmkh select @q = 'insert into #t select ma_kh from dmkh where ' + @key exec sp_executesql @q delete #t from #t a where exists(select 1 from khhddt b where a.ma_kh = b.ma_kh)
create index i on #t (ma_kh)
insert into khhddt(ma_kh, nguoi_lien_he, nguoi_dai_dien, loai_kh, status, datetime0, datetime2, user_id0, user_id2) select a.ma_kh, '', '', case when a.loai_kh <> '9' then a.loai_kh else case when rtrim(a.ma_so_thue) = '' then 0 else 1 end end, 0, getdate(), getdate(), @@userID, @@userID from dmkh a with (nolock) join #t b on a.ma_kh = b.ma_kh if object_id('dmkhdv') is null begin if exists(select 1 from dmdvcs where ma_dvcs = @ma_dvcs) insert into ctkhhddt(ma_kh, ma_dvcs, line_nbr) select a.ma_kh, @ma_dvcs, 1 from dmkh a with (nolock) join #t b on a.ma_kh = b.ma_kh end else begin select @q = 'insert into ctkhhddt(ma_kh, ma_dvcs, line_nbr) select a.ma_kh, a.ma_dvcs, a.line_nbr from dmkhdv a with (nolock) join #t b on a.ma_kh = b.ma_kh' exec sp_executesql @q end
