function init$&Identity;Filter$(f) {f.getItem('so_ct')._idle = 9;}
function active$&Identity;Filter$(f) {
  f._looking = f.getItem('so_ct')._controlBehavior;
  f._looking.add_loading(on$&Identity;Filter$Before$Loading);

  var g = f.grid, w = g.get_element().parentForm, d2 = w.getItemValue('ngay_lct'), u = w.getItemValue('ma_dvcs')&BI.IRTran.Retrieve.GetValue.PO;;
  g._custID = w.getItemValue('ma_kh');
  g._custName = w.getItemValue('ten_kh%l');
  f.setItemValues('ngay_ct2, ma_kh, ma_dvcs', [d2, g._custID, u]);
  &BI.IRTran.Retrieve.SetValue.PO;
}
function close$&Identity;Filter$(f) {try {f._looking.remove_loading(on$&Identity;Filter$Before$Loading);} catch (ex) {}}
function on$&Identity;Filter$Before$Loading(sender, e) {
  var f = sender._element.parentForm;
  if (!f.validFields('ngay_ct1')) e.type.cancelEvent = true;
  else {
    var g = f.grid, w = g.get_element().parentForm;
    var d1 = f.getItemValue('ngay_ct1'), d2 = w.getItemValue('ngay_lct'), u = w.getItemValue('ma_dvcs')&BI.IRTran.Retrieve.GetValue.PO;;

    a = [
      {Name: 'ngay_ct1', Type: 'DateTime', Value: d1},
      {Name: 'ngay_ct2', Type: 'DateTime', Value: d2},
      {Name: 'ma_kh', Type: 'String', Value: g._custID},
      {Name: 'ma_dvcs', Type: 'String', Value: u}
      &BI.IRTran.Retrieve.AddValue.PO;
    ];
    sender.set_memvars(a);
  }
}
function on$&Identity;Filter$Retrieve$QueryComplete(f, c, d, k, e, h, l) {
  var g = f.grid;
  if (c != '') {
    g._voucher$Retrieve$ID = d;
    g._voucher$Retrieve$Number = c;
    g._voucher$Retrieve$Fields = k;
    g._voucher$Retrieve$Date = $func.formatStringToDate(e);
  } else {
    g._filter$Fields = l;
  }
  set$&Identity;Filter$FormScript(g, h);
}
function set$&Identity;Filter$FormScript(g, h) {g._formScript = 'show$&Identity;Filter$QueryComplete(this,\'' + (h ? h : '') + '\')';}
function show$&Identity;Filter$QueryComplete(g, h) {
  if (h != '') {
    g.showForm(h);
  } else {
    g.showForm('&Identity;Form');
  }
  g._memvars = [];
}




