  isnull(case when month(@ngay_khai) < 10 then '0' + rtrim(month(@ngay_khai)) else rtrim(month(@ngay_khai)) end, '....') as thang_khai,
  isnull(rtrim(year(@ngay_khai)), '....') as nam_khai
