function active$VoucherFilter$(f) {
  f.add_onResponseComplete(on$Filter$ResponseComplete);
  if (f._convection) f._convection = null;
  else {
    changeLookupReadonly(f, 'ma_dvcs');
    var a = ['tk'];
    if (!f._extensions) f.request('GetDefaultValue', 'SetDefaultValue', a);
      else setReferenceValue(f, a);
  }
  if (!f.getItemValue('date_open')) f.setItemValue('date_open', new Date());
  &ReportSign.Filter.Active;
}
function close$VoucherFilter$(a){try{a.remove_onResponseComplete(on$Filter$ResponseComplete)}catch(b){}}function on$Filter$ResponseComplete(f,b){var a=b.object,e=b.type.Context,c=b.type.Result;switch(e){case"Checking":a.grid._hiddenFields=[{Fields:"ps_no_nt, ps_co_nt, ma_nt, ty_gia",Value:a.getItemValue("mau_bc")=="10"}];var d=a.getItemValue("group");a.grid._hiddenForms=[{Form:[0,1],Value:d=="0"},{Form:[2,3],Value:d=="1"}];a.grid._alterTitle=[null,[["%s1",a.getItemValue("tk"),true],["%d1",a.getItem("date_from").value,true],["%d2",a.getItem("date_to").value,true]]];remove$GridReport$Filter(a.grid);break;case"SetDefaultValue":a.setItemControlBehavior("ten_tk%l",c[0].Value,null,true);c[1].Value&&a.setItemValue("tk","")}}function setReferenceValue(a,c){for(var b=0;b<c.length;b++){var d=$func.findField(c[b],a._fields).ItemReference;a.setItemValue(d,$func.findMemvar(d,a._extensions).NewValue)}}







declare @c0 nvarchar(1024)
