function on$Filter$ResponseComplete(sender, e) {
  var f = e.object, context = e.type.Context, result = e.type.Result;
  switch (context) {
    case 'Checking':
			var g = f.grid, dFrom = f.getItem('ngay1').value, dTo = f.getItem('ngay2').value;
