select @q = 'select @sumOut = sum(tien), @sum_ntOut = sum(tien_nt) from ' + @table + ' where ma_vv = ''' + replace(@ma_vv, '''', '') + ''''
if (charindex('dmvv', @table) < 0) select @q = @q + ' and nam = ' + rtrim(cast(year(@date_from) as int))
