declare @c0 nvarchar(1024), @c1 nvarchar(1024)
select @c0 = rtrim(b.ten_dvt%l), @c1 = rtrim(a.ten_vt%l) from dmvt a left join dmdvt b on a.dvt = b.dvt where a.ma_vt = @ma_vt
select isnull(@c0, '') as c0, isnull(@c1, '') as c1
