declare @form nvarchar(4000), @name nvarchar(4000), @defaultForm varchar(33), @reportType char(1)
select @form = '', @name = ''
select @reportType = rtrim(val) from options where name = 'm_kieu_bc'
select top 1 @defaultForm = rtrim(upper(form)) from v20dmmaubc where ma_maubc = '&defaultForm;' and (kieu_bc = 0 or kieu_bc = @reportType) and nd_yn = 1
if @defaultForm is null select top 1 @defaultForm = rtrim(upper(form)) from v20dmmaubc where ma_maubc = '&defaultForm;' and (kieu_bc = 0 or kieu_bc = @reportType)

select @form = @form + rtrim(upper(form)) + char(255), @name = @name + case when lower(@@language) = 'v' then rtrim(ten_maubc) else rtrim(ten_maubc2) end + char(255) from v20dmmaubc where ma_maubc = '&defaultForm;' and (kieu_bc = 0 or kieu_bc = @reportType)
select @form = left(@form, len(@form) - 1), @name = left(@name, len(@name) - 1)
select 'this._itemValue=''' + rtrim(@form) + ''';this._itemText=''' + dbo.AI_ERP$Function$EncodeScript(@name) + '''; this._defaultForm = ''' + @defaultForm + ''';initForm(this);' as message
return







