declare @message nvarchar(4000)
select @message = 'init$VoucherFilter$(this);'
&ReportSign.Filter.Initialize;
select @message as message
return







declare @form nvarchar(4000), @name nvarchar(4000)
select @form = '', @name = ''
select @form = @form + rtrim(form) + char(255), @name = @name + case when @@language = 'v' then rtrim(ten_maubc) else rtrim(ten_maubc2) end + char(255) from v20dmmaubc where ma_maubc = '&defaultForm;'
select @form = left(@form, len(@form) - 1), @name = left(@name, len(@name) - 1)
select 'this._itemValue=''' + rtrim(@form) + ''';this._itemText=''' + dbo.AI_ERP$Function$EncodeScript(@name) + ''';initForm(this);' as message
return




