function active$VoucherFilter$(f) {
  f.add_onResponseComplete(on$Filter$ResponseComplete);
  changeLookupReadonly(f, 'ma_dvcs');
  if (!f.getItemValue('ngay_mc')) f.setItemValue('ngay_mc', new Date());
  &ReportSign.Filter.Active;
  var o = f.getItem('maxLength');
  o.value = o.maxLength;
}
function close$VoucherFilter$(a){try{a.remove_onResponseComplete(on$Filter$ResponseComplete)}catch(b){}}function on$Filter$ResponseComplete(i,c){var a=c.object,e=c.type.Context,h=c.type.Result;switch(e){case"Checking":var b=a.grid,d=a.getItemValue("mau_bc"),f=a.getItem("tu_ngay").value,g=a.getItem("den_ngay").value;b._hiddenFields=[{Fields:"tien_nt",Value:d=="10"},{Fields:"tien",Value:d=="20"}];b._alterTitle=[null,[["%d1",f,true],["%d2",g,true]]];b._queryFilterString=getQueryFilterString(a,"tu_ngay, den_ngay, ma_dvcs, mau_bc, maxLength");remove$GridReport$Filter(b)}}



  &OutlineCss;
