function onChange$&controller;$Item(o) {
  var f = o.parentForm, name = o.field.Name;
  switch (name) {
    case 'kieu_kh':
      onChange$&controller;$Type(f, true);
      break;
    case 'xoay_theo':
      onChange$&controller;$PivotBy(f, true);
      break;
    default:
      break;
  }
}
function onChange$&controller;$PivotBy(f, force) {window.setTimeout(String.format('onFocus$&controller;$PivotBy(\'{0}\', {1});', f._id, force ? 1 : 0), 2);}
function onFocus$&controller;$PivotBy(id, force) {
  var f = $find(id), x = f.getItemValue('xoay_theo'), bp = (x == 2), vv = (x == 3);
  setItem$&controller;$FieldsEmpty(f, 'ma_bp', bp)
  setItem$&controller;$Disable(f, 'ma_bp', !bp);
  setItem$&controller;$FieldsEmpty(f, 'ma_vv', vv)
  setItem$&controller;$Disable(f, 'ma_vv', !vv);
  setItem$&controller;$Disable(f, 'vvc_yn', !vv);
}
function onChange$&controller;$Type(f, force) {window.setTimeout(String.format('onFocus$&controller;$Type(\'{0}\', {1});', f._id, force ? 1 : 0), 2);}
function onFocus$&controller;$Type(id, force) {
  var f = $find(id), v = (f.getItem('kieu_kh').value == '3'), t1 = f.getItem('so_cs'), t2 = f.getItem('xoay_theo');
  setItem$&controller;$Disable(f, 'so_cs', !v);
  if (force) {
    if (v) {
      f.getItem('so_cs').disabled = false;
      f.getItem('so_cs').focus();
      f.live(f.getItem('so_cs'));
    } else {
      f.getItem('xoay_theo').focus();
      f.live(f.getItem('xoay_theo'));
    }
  }
}
function setItem$&controller;$Disable(f, c, v) {
  var a = c.split(',');
  for (var i = 0; i < a.length; i++) {
    var o = f.getItem($func.trim(a[i]));
    if (o.a) $common.setVisible(o.a, !v);
    o.disabled = v;
  }
}
function setItem$&controller;$FieldsEmpty(f, c, v) {
  var a = c.split(',');
  for (var i = 0; i < a.length; i++) {
    var o = f.getItem($func.trim(a[i]));
    if (!v) o.value = '';
  }
}
function init$&controller;$Form(f) {
  var o = f.getItem('form'), value = f._itemValue.split(String.fromCharCode(255)), text = f._itemText.split(String.fromCharCode(255)), index;
  for (var i = 0; i < value.length; i++) {
    o.options[i] = new Option(text[i], value[i]);
    if (!index && f._defaultForm == value[i]) index = i;
  }
  if (!index) index = 0;
  o.options[index].selected = true;
}




