      function active$VoucherFilter$(a){a.add_onResponseComplete(on$Filter$ResponseComplete);changeLookupReadonly(a,"ma_dvcs");changeLookupReadonly(a,"tk_dt");changeLookupReadonly(a,"tk_gg");changeLookupReadonly(a,"tk_tl");var b=a.getItem("tu_thang");if(!a.getItemValue("tu_thang")){b.value=a.getItem("den_thang").value=(new Date).getMonth()+1;a.getItem("nam").value=(new Date).getFullYear()}a.live(b)}function close$VoucherFilter$(a){try{a.remove_onResponseComplete(on$Filter$ResponseComplete)}catch(b){}}function on$Filter$ResponseComplete(k,b){var a=b.object,e=b.type.Context,j=b.type.Result;switch(e){case"Checking":var c=a.grid,h=a.getItem("mau_bc").value,d=a.getItemValue("pp_tinh_thue");c._hiddenFields=[{Fields:"tien_nt, thue_giam_nt",Value:h=="10"},{Fields:"tl_thue, tl_thue_giam",Value:d=="1"},{Fields:"thue_suat, thue_suat_giam",Value:d=="2"}];var f=a.getItem("tu_thang").value,g=a.getItem("den_thang").value,i=a.getItem("nam").value;c._alterTitle=[null,[["%m1",f,true],["%m2",g,true],["%y",i,true]]];remove$GridReport$Filter(c)}}



