declare @message nvarchar(1024)
select @message = ''
&GetReportFormStockRight;
declare @filter$Pattern varchar(128), @form nvarchar(4000), @name1 nvarchar(4000), @name2 nvarchar(4000)
select @filter$Pattern = 'this._filter$Pattern = ''' + rtrim(parameter) + ''';' from @@sysDatabaseName..wcommand where sysid = '&Identity;'
select @form = '', @name1 = '', @name2 = ''
select @form = @form + rtrim(form_id) + ',', @name1 = @name1 + rtrim(form_name) + ',', @name2 = @name2 + rtrim(form_name2) + ',' from sysrpoptions where form = 'v20sobclnkh' and form_type ='001'&Operation.And;&FieldsFilter.Edition;
if (lower(@@language) = 'v')
  select 'this._itemValue=''' + rtrim(@form) + ''';this._itemText=''' + rtrim(@name1) + ''';initForm(this);' + @filter$Pattern + @message as message
else
  select 'this._itemValue=''' + rtrim(@form) + ''';this._itemText=''' + rtrim(@name2) + ''';initForm(this);' + @filter$Pattern + @message as message
return







