exec rs_rptStockByNorm @ngay, @ma_kho, @ma_vt, @nh_vt1, @nh_vt2, @nh_vt3, @loai_vt, @c, @ma_dvcs, @dinh_muc, 'ma_vt', @loai_du_lieu, @@language, @@userID, @@admin, '#$query'

