      function init$StocktakingResultReport$(a){a.getItem("so_ct")._idle=9}function active$VoucherFilter$(a){a.add_onResponseComplete(on$Filter$ResponseComplete);a._looking=a.getItem("so_ct")._controlBehavior;a._looking.add_loading(onclick$StocktakingResultReport$Before$Loading)}function close$VoucherFilter$(a){try{a._looking.remove_loading(on$StocktakingResultReport$Before$Loading)}catch(b){}try{a.remove_onResponseComplete(on$Filter$ResponseComplete)}catch(b){}}function on$Filter$ResponseComplete(g,b){var a=b.object,e=b.type.Context,f=b.type.Result;switch(e){case"Checking":var c=a.grid,d=a.getItemValue("mau_bc");a.grid._alterTitle=[null,[["%s",a.getItemValue("so_ct").toUpperCase(),true]]];c._hiddenFields=[{Fields:"ma_vi_tri, ma_lo",Value:d=="20"}];c._hiddenForms=[{Form:[1],Value:d=="10"},{Form:[0],Value:d=="20"}];remove$GridReport$Filter(c)}}function onclick$StocktakingResultReport$Before$Loading(c,f){var b=c._element.parentForm;if(!b.validFields("ngay_ct1")||!b.validFields("ngay_ct2"))f.type.cancelEvent=true;else{var g=b.grid,h=g.get_element().parentForm,d=b.getItemValue("ngay_ct1"),e=b.getItemValue("ngay_ct2");a=[{Name:"d1",Type:"DateTime",Value:d},{Name:"d2",Type:"DateTime",Value:e}];c.set_memvars(a)}}




