function active$VoucherFilter$(f) {
  f.add_onResponseComplete(on$Filter$ResponseComplete);
  changeLookupReadonly(f, 'ma_dvcs');
  if (!f.getItemValue('thang')) setDefault$Period(f);
  f.live(f.getItem('thang'));
  &ReportSign.Filter.Active;
}
function close$VoucherFilter$(a){try{a.remove_onResponseComplete(on$Filter$ResponseComplete)}catch(b){}}function on$Filter$ResponseComplete(i,b){var a=b.object,e=b.type.Context,h=b.type.Result;switch(e){case"Checking":var c=a.grid,j=a._language=="v",f=a.getItem("thang").value,g=a.getItem("nam").value,d=a.getItemValue("mau_bc");c._alterTitle=[null,[["%d1",f,true],["%d2",g,true]]];c._hiddenForms=[{Form:[1],Value:d=="10"},{Form:[0],Value:d=="20"}]}}function setDefault$Period(a){a.getItem("thang").value=(new Date).getMonth()+1;a.getItem("nam").value=(new Date).getFullYear()}



  &OutlineCss;
