  , case when @nam <> 0 then @nam else year(@ngay1) end as nam_tc
exec rs_rptTrialBalanceSummary @ngay1, @ngay2, @ma_dvcs, @tk_so_cai_1, @tk_1, @tk_bac_1, @tk_chi_tiet, @bu_tru, @sd_1ve, @sd_2ve, @mau_bc, @@language, @@userID, @@admin

