query.text:
&CheckAuthorize;
declare @stt_rec varchar(32), @period varchar(6), @q nvarchar(4000), @controller varchar(128) select @controller = '@@controller', @stt_rec = filename, @period = description from @@sysDatabaseName..ticket where ticket = @@ticket and user_id = @@userID
select @q = 'select stt_rec, ma_ct, convert(varchar(10), a.ngay_ct, 103) as ngay_ct, ltrim(rtrim(so_ct)) as so_ct, ''' + @period + ''' as period, case when '@@language' = ''v'' then b.ten_kh else b.ten_kh2 end as ten_kh, case when '@@language' = ''v'' then c.ten_dvcs else c.ten_dvcs2 end as ten_dvcs from @@prime' + @period + ' a join dmkh b on a.ma_kh = b.ma_kh join dmdvcs c on a.ma_dvcs = c.ma_dvcs where a.stt_rec = ''' + @stt_rec + '''' exec sp_executesql @q

