if @@admin = 0 and @type = '1' begin
  if @@sysDatabaseName.dbo.AI_ERP$System$GetAuthorize(@@admin, @@userID, '&Controller;', 'Edit') = 0 select @type = '0'
end

declare @message nvarchar(4000), @q nvarchar(4000), @duplicate nvarchar(4000), @irregular nvarchar(4000), @irregularChars varchar(128), @field varchar(32), @r int

select @irregularChars = &IrregularValue;
select @irregular = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> có chứa các ký tự: ' + @irregularChars else 'The value of cell <span class="Highlight">%invalidCell</span> contains any of the following characters: ' + @irregularChars  end
select @duplicate = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> &BIArea.Nested.Message.v;.' else 'The value of cell <span class="Highlight">%invalidCell</span> is &BIArea.Nested.Message.e;.' end

create index i on @@table (ma_kv)

-- Delete
if @type = '0' delete @@table from @@table a join bidmkv b on a.ma_kv = b.ma_kv

-- Checking
&Checking;
if @$mode = 1 begin
  &IrregularMessage;
  &StartErrorCount;
  &InsertErrorTable; select 'ma_kv', stt, @message from @@table where ma_kv like '%[' + @irregularChars + ']%'
  &EndErrorCount;
end else begin
  select @field = 'ma_kv', @r = min(stt) from @@table where ma_kv like '%[' + @irregularChars + ']%'&Irregular;
end

-- Gather
select a.* into #k from @@table a join bidmkv b on a.ma_kv = b.ma_kv

-- Do not delete following line
-- #OverwriteChecking

if @type = '1' delete @@table from @@table a where exists(select 1 from #k b where a.ma_kv = b.ma_kv)

-- Checking
select @field = 'ma_kv'
&BIArea.Nested.Key.b;
  if @$mode = 1 begin
    &StartErrorCount;
    select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a, @@table b with (index(i)) where a.ma_kv <> b.ma_kv and a.ma_kv like rtrim(b.ma_kv) + ''%''
      &w1;@field&w2;a.stt&w3;''00003''&w4; group by a.stt'
    exec sp_executesql @q, N'@field varchar(32)', @field
    &EndErrorCount;
    &StartErrorCount;
    select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a with (index(i)), bidmkv b with (index(PK_bidmkv)) where a.ma_kv like rtrim(b.ma_kv) + ''%''
      &w1;@field&w2;a.stt&w3;''00003''&w4; group by a.stt'
    exec sp_executesql @q, N'@field varchar(32)', @field
    &EndErrorCount;
    &StartErrorCount;
    select @q = '&InsertErrorTable; select @field, b.stt, ''00003'' from bidmkv a with (index(PK_bidmkv)), @@table b with (index(i)) where a.ma_kv like rtrim(b.ma_kv) + ''%''
      &w1;@field&w2;b.stt&w3;''00003''&w4; group by b.stt'
    exec sp_executesql @q, N'@field varchar(32)', @field
    &EndErrorCount;
  end else begin
    select @q = 'select top 1 @r = a.stt from @@table a, @@table b with (index(i)) where a.ma_kv <> b.ma_kv and a.ma_kv like rtrim(b.ma_kv) + ''%'''
    exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
    select @q = 'select top 1 @r = a.stt from @@table a with (index(i)), bidmkv b with (index(PK_bidmkv)) where a.ma_kv like rtrim(b.ma_kv) + ''%'''
    exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
    select @q = 'select top 1 @r = b.stt from bidmkv a with (index(PK_bidmkv)), @@table b with (index(i)) where a.ma_kv like rtrim(b.ma_kv) + ''%'''
    exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
  end
end
&EndErrorMode;

-- Insert
&Inserting;

-- Update
if @type = '1' begin
  &ListWhenBeforeImportUpdateLog;
  &Updating;
end
