  if @@sysDatabaseName.dbo.AI_ERP$System$GetAuthorize(@@admin, @@userID, '&Controller;', 'Edit') = 0 select @type = '0'
end

declare @message nvarchar(4000), @q nvarchar(4000), @duplicate nvarchar(4000), @irregular nvarchar(4000), @irregularChars varchar(128), @field varchar(32), @r int

select @irregularChars = &IrregularValue;
select @irregular = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> có chứa các ký tự: ' + @irregularChars else 'The value of cell <span class="Highlight">%invalidCell</span> contains any of the following characters: ' + @irregularChars  end
select @duplicate = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> &BIConditionType.Nested.Message.v;.' else 'The value of cell <span class="Highlight">%invalidCell</span> is &BIConditionType.Nested.Message.e;.' end

create index i on @@table (ma_loai)

if @type = '0' delete @@table from @@table a join bidmldkg b on a.ma_loai = b.ma_loai

update @@table set tinh_theo = 0 where kieu_tinh <> 1

if @$mode = 1 begin
  &IrregularMessage;
  &StartErrorCount;
  &InsertErrorTable; select 'ma_loai', stt, @message from @@table where ma_loai like '%[' + @irregularChars + ']%'
  &EndErrorCount;
end else begin
  select @field = 'ma_loai', @r = min(stt) from @@table where ma_loai like '%[' + @irregularChars + ']%'&Irregular;
end

-- Checking
&Checking;

select a.* into #k from @@table a join bidmldkg b with (nolock) on a.ma_loai = b.ma_loai
delete @@table where ma_loai in (select ma_loai from #k)
if @$mode = 1 begin
  &StartErrorCount;
  if @type = '1' &InsertErrorTable; select 'ma_loai', stt, '00001' from #k where ma_loai not in (select ma_loai from bidmldkg with (nolock))&w1;'ma_loai'&w2;stt&w3;'00001'&w4;
  &EndErrorCount;
end else begin
  if @type = '1' select @field = 'ma_loai', @r = min(stt) from #k where ma_loai not in (select ma_loai from bidmldkg with (nolock))&Error;
end

