select @field = 'ma_loai'
&BIConditionType.Nested.Key.b;
  if @$mode = 1 begin
    &StartErrorCount;
    select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a, @@table b with (index(i)) where a.ma_loai <> b.ma_loai and a.ma_loai like rtrim(b.ma_loai) + ''%''
      &w1;@field&w2;a.stt&w3;''00003''&w4; group by a.stt'
    exec sp_executesql @q, N'@field varchar(32)', @field
    &EndErrorCount;
    &StartErrorCount;
    select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a with (index(i)), bidmldkg b with (nolock index(PK_bidmldkg)) where a.ma_loai like rtrim(b.ma_loai) + ''%''
      &w1;@field&w2;a.stt&w3;''00003''&w4; group by a.stt'
    exec sp_executesql @q, N'@field varchar(32)', @field
    &EndErrorCount;
    &StartErrorCount;
    select @q = '&InsertErrorTable; select @field, b.stt, ''00003'' from bidmldkg a with (nolock index(PK_bidmldkg)), @@table b with (index(i)) where a.ma_loai like rtrim(b.ma_loai) + ''%''
      &w1;@field&w2;b.stt&w3;''00003''&w4; group by b.stt'
    exec sp_executesql @q, N'@field varchar(32)', @field
    &EndErrorCount;
  end else begin
    select @q = 'select top 1 @r = a.stt from @@table a, @@table b with (index(i)) where a.ma_loai <> b.ma_loai and a.ma_loai like rtrim(b.ma_loai) + ''%'''
    exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
    select @q = 'select top 1 @r = a.stt from @@table a with (index(i)), bidmldkg b with (nolock index(PK_bidmldkg)) where a.ma_loai like rtrim(b.ma_loai) + ''%'''
    exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
    select @q = 'select top 1 @r = b.stt from bidmldkg a with (nolock index(PK_bidmldkg)), @@table b with (index(i)) where a.ma_loai like rtrim(b.ma_loai) + ''%'''
    exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
  end
