if @@admin = 0 and @type = '1' begin
  if @@sysDatabaseName.dbo.AI_ERP$System$GetAuthorize(@@admin, @@userID, 'BIDefectCode', 'Edit') = 0 select @type = '0'
end

declare @message nvarchar(4000), @q nvarchar(4000), @duplicate nvarchar(4000), @irregular nvarchar(4000), @irregularChars varchar(128), @field varchar(32), @r int, @d datetime

select @irregularChars = &IrregularValue;, @d = getDate()
select @irregular = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> có chứa các ký tự: ' + @irregularChars else 'The value of cell <span class="Highlight">%invalidCell</span> contains any of the following characters: ' + @irregularChars  end
select @duplicate = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> &Nested.Message.v;.' else 'The value of cell <span class="Highlight">%invalidCell</span> is &Nested.Message.e;.' end

create index i on @@table (ma_loi)

-- Delete
if @type = '0' delete @@table from @@table a join bikddmloi b on a.ma_loi = b.ma_loi

-- Checking
&Checking;

select @field = 'ma_loi'
if @$mode = 1 begin
  &IrregularMessage;
  &StartErrorCount;
  &InsertErrorTable; select @field, stt, @message from @@table where ma_loi like '%[' + @irregularChars + ']%'
  &EndErrorCount;
end else begin
  select @r = min(stt) from @@table where ma_loi like '%[' + @irregularChars + ']%'&Irregular;
end

-- Gather
select a.* into #k from @@table a join bikddmloi b on a.ma_loi = b.ma_loi
delete @@table from @@table a join #k b on a.ma_loi = b.ma_loi

-- Checking
select @field = 'ma_loi'
&Nested.Key.b;
  if @$mode = 1 begin
    &StartErrorCount;
    select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a, @@table b with (index(i)) where a.ma_loi <> b.ma_loi and a.ma_loi like rtrim(b.ma_loi) + ''%''
    &w1;@field&w2;a.stt&w3;''00003''&w4; group by a.stt'
    exec sp_executesql @q, N'@field varchar(32)', @field
    &EndErrorCount;
    &StartErrorCount;
    select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a, bikddmloi b with (index(PK_bikddmloi)) where a.ma_loi like rtrim(b.ma_loi) + ''%''
    &w1;@field&w2;a.stt&w3;''00003''&w4; group by a.stt'
    exec sp_executesql @q, N'@field varchar(32)', @field
    &EndErrorCount;
    &StartErrorCount;
    select @q = '&InsertErrorTable; select @field, b.stt, ''00003'' from bikddmloi a, @@table b with (index(i)) where a.ma_loi like rtrim(b.ma_loi) + ''%''
    &w1;@field&w2;b.stt&w3;''00003''&w4; group by b.stt'
    exec sp_executesql @q, N'@field varchar(32)', @field
    &EndErrorCount;
  end else begin
    select @q = 'select top 1 @r = a.stt from @@table a, @@table b with (index(i)) where a.ma_loi <> b.ma_loi and a.ma_loi like rtrim(b.ma_loi) + ''%'''
    exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
    select top 1 @r = a.stt from @@table a, bikddmloi b with (index(PK_bikddmloi)) where a.ma_loi like rtrim(b.ma_loi) + '%'&Duplicate;
    select @q = 'select top 1 @r = b.stt from bikddmloi a, @@table b with (index(i)) where a.ma_loi like rtrim(b.ma_loi) + ''%'''
    exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
  end
end
&EndErrorMode;

-- Insert
&Inserting;
-- Update
if @type = '1' begin
  &ListWhenBeforeImportUpdateLog;
  &Updating;
end
