declare @message nvarchar(4000), @q nvarchar(4000), @duplicate nvarchar(4000), @irregular nvarchar(4000), @irregularChars varchar(128), @field varchar(32), @r int

select @irregularChars = &IrregularValue;
select @irregular = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> có chứa các ký tự: ' + @irregularChars else 'The value of cell <span class="Highlight">%invalidCell</span> contains any of the following characters: ' + @irregularChars  end
select @duplicate = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> &BIPlant.Nested.Message.v;.' else 'The value of cell <span class="Highlight">%invalidCell</span> is &BIPlant.Nested.Message.e;.' end

create index i on @@table (ma_vung)

-- Delete
if @type = '0' delete @@table from @@table a join bidmvung b on a.ma_vung = b.ma_vung

-- Checking
&Checking;
&CheckValidEmailCommandCheckingImport;

select @field = 'ma_vung'
if @$mode = 1 begin
  &IrregularMessage;
  &StartErrorCount;
  &InsertErrorTable; select @field, stt, @message from @@table where ma_vung like '%[' + @irregularChars + ']%'
  &EndErrorCount;
end else begin
  select @r = min(stt) from @@table where ma_vung like '%[' + @irregularChars + ']%'&Irregular;
end

-- Gather
select a.* into #k from @@table a join bidmvung b on a.ma_vung = b.ma_vung
delete @@table where ma_vung in (select ma_vung from #k)
select identity(int, 1, 1) as stt, ma_dvcs, ma_vung, ten_vung, ten_vung2, dia_chi, dien_thoai, fax, e_mail, ghi_chu into #k1 from @@table

-- Checking
select @field = 'ma_vung'
&BIPlant.Nested.Key.b;
  if @$mode = 1 begin
    &StartErrorCount;
    select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a, @@table b with (index(i)) where a.ma_vung <> b.ma_vung and a.ma_vung like rtrim(b.ma_vung) + ''%''
    &w1;@field&w2;a.stt&w3;''00003''&w4; group by a.stt'
    exec sp_executesql @q, N'@field varchar(32)', @field
    &EndErrorCount;
    &StartErrorCount;
    select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a, bidmvung b with (index(PK_bidmvung)) where a.ma_vung like rtrim(b.ma_vung) + ''%''
    &w1;@field&w2;a.stt&w3;''00003''&w4; group by a.stt'
    exec sp_executesql @q, N'@field varchar(32)', @field
    &EndErrorCount;
    &StartErrorCount;
    select @q = '&InsertErrorTable; select @field, b.stt, ''00003'' from bidmvung a, @@table b with (index(i)) where a.ma_vung like rtrim(b.ma_vung) + ''%''
    &w1;@field&w2;b.stt&w3;''00003''&w4; group by b.stt'
    exec sp_executesql @q, N'@field varchar(32)', @field
    &EndErrorCount;
  end else begin
    select @q = 'select top 1 @r = a.stt from @@table a, @@table b with (index(i)) where a.ma_vung <> b.ma_vung and a.ma_vung like rtrim(b.ma_vung) + ''%'''
    exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
    select top 1 @r = a.stt from @@table a, bidmvung b with (index(PK_bidmvung)) where a.ma_vung like rtrim(b.ma_vung) + '%'&Duplicate;
    select @q = 'select top 1 @r = b.stt from bidmvung a, @@table b with (index(i)) where a.ma_vung like rtrim(b.ma_vung) + ''%'''
    exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
  end
end

