declare @q nvarchar(4000), @field varchar(32), @r int, @maxLength int, @costUnit char(1), @dateCurrent smalldatetime
select @dateCurrent = dateadd(mm, 1, cast(str(@$nam, 4) + replace(str(@$ky, 2), ' ', '0') + '01' as smalldatetime)) - 1 select @maxLength = character_maximum_length from information_schema.columns where table_name = 'xdmcdsp' and column_name = 'so_lsx' select @costUnit = rtrim(val) from options where name = 'm_gt_dvcs'
-- Update update @@table set so_lsx = space(@maxLength - len(rtrim(so_lsx))) + rtrim(so_lsx)
-- Delete delete @@table from @@table a where exists(select 1 from xdmcdsp b where a.ma_dvcs = b.ma_dvcs and a.ma_yt = b.ma_yt and a.ma_bp0 = b.ma_bp0 and a.ma_bp = b.ma_bp and a.so_lsx = b.so_lsx and a.ma_sp = b.ma_sp and b.nam = @$nam and b.ky = @$ky)
-- Checking &Checking; &EndErrorMode;
-- Insert &Inserting;
