declare @q nvarchar(4000), @message nvarchar(4000), @field varchar(32), @user int, @admin bit, @r int, @unit varchar(32)
select @admin = @@admin, @user = @@userID, @unit = @@unit

-- Checking
&Checking;

if @admin <> 1 begin
  if @type = '1' begin
    if @@sysDatabaseName.dbo.AI_ERP$System$GetAuthorize(@admin, @user, 'CRProductionLeadtimeUnit', 'Edit') = 0 select @type = '0'
  end
end

-- Update
update @@table set dvt = case when a.dvt <> '' then a.dvt else b.dvt end from @@table a left join dmvt b on a.ma_vt = b.ma_vt

select @field = 'dvt'
if @$mode = 1 begin
  &StartErrorCount;
  &InsertErrorTable; select @field, stt, '00001' from @@table a left join vdmvtqddvt b on a.dvt = b.dvt and (a.ma_vt = b.ma_vt or b.ma_vt = '*') left join dmvt c on a.ma_vt = c.ma_vt and a.dvt = c.dvt where a.dvt <> '' and b.dvt is null and c.dvt is null
  &w1;@field&w2;stt&w3;'00001'&w4;
  &EndErrorCount;
end else begin
  select @field = 'dvt', @r = min(stt) from @@table a left join vdmvtqddvt b on a.dvt = b.dvt and (a.ma_vt = b.ma_vt or b.ma_vt = '*') left join dmvt c on a.ma_vt = c.ma_vt and a.dvt = c.dvt where a.dvt <> '' and b.dvt is null and c.dvt is null&Error;
end

if @type = '0' delete @@table from @@table a where exists(select 1 from crdmtgsp b where &k;)

select ma_vt, ngay_hl, cong_suat, 1 as he_so into #r from @@table
update #r set he_so = isnull(b.he_so, 1) from @@table a join vdmvtqddvt b on (a.ma_vt = b.ma_vt or b.ma_vt = '*') and a.dvt = b.dvt left join dmvt c on a.ma_vt = c.ma_vt where c.nhieu_dvt = 1

select a.* into #k from @@table a join crdmtgsp b on &k;
delete @@table from @@table a where exists(select 1 from #k b where &k;)

&EndErrorMode;

-- Insert
&Inserting;

-- Update
if @type = '1' begin
  &Updating; where &k;
end

update crdmtgsp set he_so = b.he_so, cs_qd = b.cong_suat from crdmtgsp a join #r b on &k;
