processing.text:

if @@admin = 0 and @type = '1' begin
  if @@sysDatabaseName.dbo.AI_ERP$System$GetAuthorize(@@admin, @@userID, 'Expense', 'Edit') = 0 select @type = '0'
end

declare @message nvarchar(4000), @q nvarchar(4000), @duplicate nvarchar(4000), @irregular nvarchar(4000), @irregularChars varchar(128), @field varchar(32), @r int

select @irregularChars = &IrregularValue;
select @irregular = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> có chứa các ký tự: ' + @irregularChars else 'The value of cell <span class="Highlight">%invalidCell</span> contains any of the following characters: ' + @irregularChars  end
select @duplicate = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> &Expense.Nested.Message.v;.' else 'The value of cell <span class="Highlight">%invalidCell</span> is &Expense.Nested.Message.e;.' end

create index i on @@table (ma_phi)

-- Delete
if @type = '0' delete @@table from @@table a join dmphi b on a.ma_phi = b.ma_phi

-- Checking
&Checking;
if @$mode = 1 begin
  &IrregularMessage;
  &StartErrorCount;
  &InsertErrorTable; select 'ma_phi', stt, @message from @@table where ma_phi like '%[' + @irregularChars + ']%'
  &EndErrorCount;
end else begin
  select @field = 'ma_phi', @r = min(stt) from @@table where ma_phi like '%[' + @irregularChars + ']%'&Irregular;
end

-- Gather
select a.* into #k from @@table a join dmphi b on a.ma_phi = b.ma_phi

-- Do not delete following line
-- #OverwriteChecking

if @type = '1' delete @@table from @@table a where exists(select 1 from #k b where a.ma_phi = b.ma_phi)

-- Checking
select @field = 'ma_phi'
&Expense.Nested.Key.b;
  if @$mode = 1 begin
    &StartErrorCount;
    select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a, @@table b with (index(i)) where a.ma_phi <> b.ma_phi and a.ma_phi like rtrim(b.ma_phi) + ''%''
      &w1;@field&w2;a.stt&w3;''00003''&w4; group by a.stt'
    exec sp_executesql @q, N'@field varchar(32)', @field
    &EndErrorCount;
    &StartErrorCount;
    select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a, dmphi b with (index(PK_dmphi)) where a.ma_phi like rtrim(b.ma_phi) + ''%''
    &w1;@field&w2;a.stt&w3;''00003''&w4; group by a.stt'
    exec sp_executesql @q, N'@field varchar(32)', @field
    &EndErrorCount;
    &StartErrorCount;
    select @q = '&InsertErrorTable; select @field, b.stt, ''00003'' from dmphi a, @@table b with (index(i)) where a.ma_phi like rtrim(b.ma_phi) + ''%''
    &w1;@field&w2;b.stt&w3;''00003''&w4; group by b.stt'
    exec sp_executesql @q, N'@field varchar(32)', @field
    &EndErrorCount;
  end else begin
    select @q = 'select top 1 @r = a.stt from @@table a, @@table b with (index(i)) where a.ma_phi <> b.ma_phi and a.ma_phi like rtrim(b.ma_phi) + ''%'''
    exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
    select @q = 'select top 1 @r = a.stt from @@table a with (index(i)), dmphi b with (index(PK_dmphi)) where a.ma_phi like rtrim(b.ma_phi) + ''%'''
    exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
    select @q = 'select top 1 @r = b.stt from dmphi a with (index(PK_dmphi)), @@table b with (index(i)) where a.ma_phi like rtrim(b.ma_phi) + ''%'''
    exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
  end
end

&EndErrorMode;

-- Insert
&Inserting;

-- Update
if @type = '1' begin
  &ListWhenBeforeImportUpdateLog;
  &Updating;
end
