processing.text:

declare @message nvarchar(4000), @q nvarchar(4000), @field varchar(32), @r int, @user int, @admin bit, @unit varchar(32), @year int
select @admin = @@admin, @user = @@userID, @unit = @@unit, @year = nam_bd from dmstt

-- Checking
select @field = 'ma_dvcs'
if @$mode = 1 begin
  &StartErrorCount;
  &InsertErrorTable; select @field, stt, '00001' from @@table where ma_dvcs not in (select ma_dvcs from dmdvcs where status = '1')
  &w1;@field&w2;stt&w3;'00001'&w4;
  &EndErrorCount;
  &StartErrorCount;
  &InsertErrorTable; select @field, stt, '00002' from @@table where ma_dvcs in (select distinct ma_dvcs from dmdvcsks where year(ngay_ks) >= @year)
  &w1;@field&w2;stt&w3;'00002'&w4;
  &EndErrorCount;
end else begin
  select @r = min(stt) from @@table where ma_dvcs not in (select ma_dvcs from dmdvcs where status = '1')&Error;
  select @r = min(stt) from @@table where ma_dvcs in (select distinct ma_dvcs from dmdvcsks where year(ngay_ks) >= @year)&Error;
end

if @admin <> 1 begin
  if @type = '1' begin
    if @@sysDatabaseName.dbo.AI_ERP$System$GetAuthorize(@@admin, @@userID, 'LoanBalance', 'Edit') = 0 select @type = '0'
  end
  if @$mode = 1 begin
    &StartErrorCount;
    &InsertErrorTable; select @field, stt, '00002' from @@table a where not exists(select 1 from cdku b where a.ma_dvcs = b.ma_dvcs and a.tk = b.tk and a.ma_ku = b.ma_ku and b.nam = @year)
      and a.ma_dvcs not in (select distinct ma_dvcs from sysunitrights where user_id = @user and r_new = 1)&w1;@field&w2;stt&w3;'00002'&w4;
    &EndErrorCount;
    &StartErrorCount;
    if @type = '1' begin
      &InsertErrorTable; select @field, stt, '00002' from @@table a where exists(select 1 from cdku b where a.ma_dvcs = b.ma_dvcs and a.tk = b.tk and a.ma_ku = b.ma_ku and b.nam = @year)
        and a.ma_dvcs not in (select distinct ma_dvcs from sysunitrights where user_id = @user and r_edit = 1)&w1;@field&w2;stt&w3;'00002'&w4;
      &EndErrorCount;
    end
  end else begin
    select @r = min(a.stt) from @@table a where not exists(select 1 from cdku b where a.ma_dvcs = b.ma_dvcs and a.tk = b.tk and a.ma_ku = b.ma_ku and b.nam = @year)
      and a.ma_dvcs not in (select distinct ma_dvcs from sysunitrights where user_id = @user and r_new = 1)&Error;

    if @type = '1' begin
      select @r = min(stt) from @@table a where exists(select 1 from cdku b where a.ma_dvcs = b.ma_dvcs and a.tk = b.tk and a.ma_ku = b.ma_ku and b.nam = @year)
        and a.ma_dvcs not in (select distinct ma_dvcs from sysunitrights where user_id = @user and r_edit = 1)&Error;
    end
  end
end

-- Check
&Checking;

&EndErrorMode;

update @@table set du_co00 = 0 where du_no00 <> 0
update @@table set du_co_nt00 = 0 where du_no_nt00 <> 0

-- Delete
if @type = '0' delete @@table from @@table a where exists(select 1 from cdku b where a.ma_dvcs = b.ma_dvcs and a.tk = b.tk and a.ma_ku = b.ma_ku and b.nam = @year)

-- Gather
select a.* into #k from @@table a join cdku b on a.ma_dvcs = b.ma_dvcs and a.tk = b.tk and a.ma_ku = b.ma_ku where b.nam = @year
delete @@table from @@table a where exists(select 1 from #k b where a.ma_dvcs = b.ma_dvcs and a.tk = b.tk and a.ma_ku = b.ma_ku)
-- Insert
&Inserting;
-- Update
if @type = '1' begin
  update cdku set @@list from cdku a, #k b where a.ma_dvcs = b.ma_dvcs and a.tk = b.tk and a.ma_ku = b.ma_ku and a.nam = @year
end
