    end
  end

  &SiteRight;
end

-- Update
update @@table set dvt = case when a.dvt <> '' then a.dvt else b.dvt end from @@table a left join dmvt b on a.ma_sp = b.ma_vt

select @field = 'dvt'
if @$mode = 1 begin
  &StartErrorCount;
  &InsertErrorTable; select @field, stt, '00001' from @@table a left join vdmvtqddvt b on a.dvt = b.dvt and (a.ma_sp = b.ma_vt or b.ma_vt = '*') left join dmvt c on a.ma_sp = c.ma_vt and a.dvt = c.dvt where a.dvt <> '' and b.dvt is null and c.dvt is null
  &w1;@field&w2;stt&w3;'00001'&w4;
