processing.text:

if @@admin = 0 and @type = '1' begin
  if @@sysDatabaseName.dbo.AI_ERP$System$GetAuthorize(@@admin, @@userID, '&TransferID;', 'Edit') = 0 select @type = '0'
end

declare @message nvarchar(4000), @q nvarchar(4000), @duplicate nvarchar(4000), @irregular nvarchar(4000), @irregularChars varchar(128), @field varchar(32), @r int, @d datetime

select @irregularChars = &IrregularValue;, @d = getDate()
select @irregular = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> có chứa các ký tự: ' + @irregularChars else 'The value of cell <span class="Highlight">%invalidCell</span> contains any of the following characters: ' + @irregularChars  end
select @duplicate = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> đã có hoặc lồng nhau.' else 'The value of cell <span class="Highlight">%invalidCell</span> is invalid or already exists.' end

create index i on @@table (ma_nh)

-- Delete
if @type = '0' delete @@table from @@table a join &Table; b on a.loai_nh = b.loai_nh and a.ma_nh = b.ma_nh

-- Checking
if @$mode = 1 begin
  &IrregularMessage;
  &StartErrorCount;
  &InsertErrorTable; select 'ma_nh', stt, @message from @@table where ma_nh like '%[' + @irregularChars + ']%'
  &EndErrorCount;
end else begin
  select @field = 'ma_nh', @r = min(stt) from @@table where ma_nh like '%[' + @irregularChars + ']%'&Irregular;
end

-- Gather
select a.* into #k from @@table a join &Table; b on a.loai_nh = b.loai_nh and a.ma_nh = b.ma_nh
delete @@table from @@table a join #k b on a.loai_nh = b.loai_nh and a.ma_nh = b.ma_nh

-- Checking
&Checking;
select @field = 'ma_nh'
if @$mode = 1 begin
  &StartErrorCount;
  select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a, @@table b with (index(i)) where a.loai_nh = b.loai_nh and a.ma_nh <> b.ma_nh and a.ma_nh like rtrim(b.ma_nh) + ''%''
    &w1;@field, ''@@primaryKeyList''&w2;a.stt&w3;''00003''&w4; group by a.stt'
  exec sp_executesql @q, N'@field varchar(32)', @field
  &EndErrorCount;
  &StartErrorCount;
  select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a, &Table; b with (index(PK_&Table;)) where a.loai_nh = b.loai_nh and a.ma_nh like rtrim(b.ma_nh) + ''%''
    &w1;@field, ''@@primaryKeyList''&w2;a.stt&w3;''00003''&w4; group by a.stt'
  exec sp_executesql @q, N'@field varchar(32)', @field
  &EndErrorCount;
  &StartErrorCount;
  select @q = '&InsertErrorTable; select @field, b.stt, ''00003'' from &Table; a, @@table b with (index(i)) where a.loai_nh = b.loai_nh and a.ma_nh like rtrim(b.ma_nh) + ''%''
    &w1;@field, ''@@primaryKeyList''&w2;b.stt&w3;''00003''&w4; group by b.stt'
  exec sp_executesql @q, N'@field varchar(32)', @field
  &EndErrorCount;
end else begin
  select @q = 'select top 1 @r = a.stt from @@table a, @@table b with (index(i)) where a.loai_nh = b.loai_nh and a.ma_nh <> b.ma_nh and a.ma_nh like rtrim(b.ma_nh) + ''%'''
  exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
  select top 1 @r = a.stt from @@table a, &Table; b with (index(PK_&Table;)) where a.loai_nh = b.loai_nh and a.ma_nh like rtrim(b.ma_nh) + '%'&Duplicate;
  select @q = 'select top 1 @r = b.stt from &Table; a, @@table b with (index(i)) where a.loai_nh = b.loai_nh and a.ma_nh like rtrim(b.ma_nh) + ''%'''
  exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
end

&EndErrorMode;

-- Insert
&Inserting;
-- Update
if @type = '1' begin
  &ListWhenBeforeImportUpdateLog;
  &Updating;
end
