declare @q nvarchar(4000), @message nvarchar(4000), @field varchar(32), @user int, @admin bit, @r int
select @admin = @@admin, @user = @@userID

-- Checking
&Checking;

select @field = 'ma_dvcs'
if @$mode = 1 begin
  &StartErrorCount;
  &InsertErrorTable; select @field, stt, '00001' from @@table where ma_dvcs not in (select ma_dvcs from dmdvcs where status = '1')
  &w1;@field&w2;stt&w3;'00001'&w4;
  &EndErrorCount;
end else begin
  select @r = min(stt) from @@table where ma_dvcs not in (select ma_dvcs from dmdvcs where status = '1')&Error;
end
if @$mode = 1 begin
  &StartErrorCount;
  &InsertErrorTable; select @field, stt, '00001' from @@table a where exists (select 1 from dmdvcsks b where year(b.ngay_ks) >= a.nam and a.ma_dvcs = b.ma_dvcs)
  &w1;@field&w2;stt&w3;'00001'&w4;
  &EndErrorCount;
end else begin
  select @r = min(stt) from @@table a where exists (select 1 from dmdvcsks b where year(b.ngay_ks) >= a.nam and a.ma_dvcs = b.ma_dvcs)&Error;
end
if @admin <> 1 begin
  if @type = '1' begin
    if @@sysDatabaseName.dbo.AI_ERP$System$GetAuthorize(@admin, @user, 'ProjectCostEstimation', 'Edit') = 0 select @type = '0'
  end

  if @$mode = 1 begin
    &StartErrorCount;
    &InsertErrorTable; select @field, a.stt, '00001' from @@table a where not exists(select 1 from dtdtvv b where a.ma_dvcs = b.ma_dvcs and a.ma_vv = b.ma_vv and b.nam = a.nam) and a.ma_dvcs not in (select distinct ma_dvcs from sysunitrights where user_id = @user and r_new = 1)
    &w1;@field&w2;a.stt&w3;'00001'&w4;
    &EndErrorCount;
  end else begin
    select @r = min(a.stt) from @@table a where not exists(select 1 from dtdtvv b where a.ma_dvcs = b.ma_dvcs and a.ma_vv = b.ma_vv and b.nam = a.nam)
      and a.ma_dvcs not in (select distinct ma_dvcs from sysunitrights where user_id = @user and r_new = 1)&Error;
  end
  if @type = '1' begin
    if @$mode = 1 begin
      &StartErrorCount;
      &InsertErrorTable; select @field, a.stt, '00001' from @@table a where exists(select 1 from dtdtvv b where a.ma_dvcs = b.ma_dvcs and a.ma_vv = b.ma_vv and b.nam = a.nam) and a.ma_dvcs not in (select distinct ma_dvcs from sysunitrights where user_id = @user and r_edit = 1)
      &w1;@field&w2;a.stt&w3;'00001'&w4;
      &EndErrorCount;
    end else begin
      select @r = min(stt) from @@table a where exists(select 1 from dtdtvv b where a.ma_dvcs = b.ma_dvcs and a.ma_vv = b.ma_vv and b.nam = a.nam)
        and a.ma_dvcs not in (select distinct ma_dvcs from sysunitrights where user_id = @user and r_edit = 1)&Error;
    end
  end
end
&EndErrorMode;

if @type = '0' delete @@table from @@table a where exists(select 1 from dtdtvv b where a.ma_dvcs = b.ma_dvcs and a.ma_vv = b.ma_vv and b.nam = a.nam)

select a.* into #k from @@table a join dtdtvv b on a.ma_dvcs = b.ma_dvcs and a.ma_vv = b.ma_vv and b.nam = a.nam
delete @@table from @@table a where exists(select 1 from #k b where a.ma_dvcs = b.ma_dvcs and a.ma_vv = b.ma_vv and a.nam = b.nam)

-- Insert
&Inserting;
-- Update
if @type = '1' begin &Updating; end
