processing.text:

if @@admin = 0 and @type = '1' begin
  if @@sysDatabaseName.dbo.AI_ERP$System$GetAuthorize(@@admin, @@userID, 'SFCShop', 'Edit') = 0 select @type = '0'
end

declare @q nvarchar(4000), @duplicate nvarchar(4000), @irregular nvarchar(4000), @irregularChars varchar(128), @field varchar(32), @message nvarchar(4000), @r int, @d datetime, @user int, @admin bit, @unit varchar(32)
select @admin = @@admin, @user = @@userID, @unit = @@unit

select @irregularChars = &IrregularValue;, @d = getDate()
select @irregular = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> có chứa các ký tự: ' + @irregularChars else 'The value of cell <span class="Highlight">%invalidCell</span> contains any of the following characters: ' + @irregularChars  end
select @duplicate = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> đã có hoặc lồng nhau.' else 'The value of cell <span class="Highlight">%invalidCell</span> is invalid or already exists.' end

create index i on @@table (ma_px)

-- Delete
if @type = '0' delete @@table from @@table a join sfdmxuong b on a.ma_px = b.ma_px

-- Checking
&Checking;

if @$mode = 1 begin
  &IrregularMessage;
  &StartErrorCount;
  &InsertErrorTable; select 'ma_px', stt, @message from @@table where ma_px like '%[' + @irregularChars + ']%'
  &EndErrorCount;
end else begin
  select @field = 'ma_px', @r = min(stt) from @@table where ma_px like '%[' + @irregularChars + ']%'&Irregular;
end

select @field = 'ma_kho'
if @$mode = 1 begin
  &StartErrorCount;
  &InsertErrorTable; select @field, stt, '00001' from @@table a where not exists(select 1 from sfdmxuong b where a.ma_px = b.ma_px) and not exists(select 1 from dmkho c where a.ma_kho = c.ma_kho and c.ma_dvcs in (select distinct ma_dvcs from sysunitrights where user_id = @user and r_new = 1))
  &w1;@field&w2;stt&w3;'00001'&w4;
  &EndErrorCount;

  if @type = '1' begin
    &StartErrorCount;
    &InsertErrorTable; select @field, stt, '00001' from @@table a where exists(select 1 from sfdmxuong b where a.ma_px = b.ma_px) and not exists(select 1 from dmkho c where a.ma_kho = c.ma_kho and c.ma_dvcs in (select distinct ma_dvcs from sysunitrights where user_id = @user and r_edit= 1))
    &w1;@field&w2;stt&w3;'00001'&w4;
    &EndErrorCount;
  end
end else begin
  select @r = min(stt) from @@table a where not exists(select 1 from sfdmxuong b where a.ma_px = b.ma_px)
    and not exists(select 1 from dmkho c where a.ma_kho = c.ma_kho and c.ma_dvcs in (select distinct ma_dvcs from sysunitrights where user_id = @user and r_new = 1))&Error;

  if @type = '1' begin
      select @r = min(a.stt) from @@table a where exists(select 1 from sfdmxuong b where a.ma_px = b.ma_px)
        and not exists(select 1 from dmkho c where a.ma_kho = c.ma_kho and c.ma_dvcs in (select distinct ma_dvcs from sysunitrights where user_id = @user and r_edit= 1))&Error;
  end
end

-- Gather
select a.* into #k from @@table a join sfdmxuong b on a.ma_px = b.ma_px
delete @@table from @@table a join #k b on a.ma_px = b.ma_px

-- Checking
select @field = 'ma_px'
if @$mode = 1 begin
  &StartErrorCount;
  select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a, @@table b with (index(i)) where a.ma_px <> b.ma_px and a.ma_px like rtrim(b.ma_px) + ''%''
  &w1;@field&w2;a.stt&w3;''00003''&w4; group by a.stt'
  exec sp_executesql @q, N'@field varchar(32)', @field
  &EndErrorCount;
  &StartErrorCount;
  select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a with (index(i)), sfdmxuong b with (index(PK_sfdmxuong)) where a.ma_px like rtrim(b.ma_px) + ''%''
  &w1;@field&w2;a.stt&w3;''00003''&w4; group by a.stt'
  exec sp_executesql @q, N'@field varchar(32)', @field
  &EndErrorCount;
  &StartErrorCount;
  select @q = '&InsertErrorTable; select @field, b.stt, ''00003'' from sfdmxuong a with (index(PK_sfdmxuong)), @@table b with (index(i)) where a.ma_px like rtrim(b.ma_px) + ''%''
  &w1;@field&w2;b.stt&w3;''00003''&w4; group by b.stt'
  exec sp_executesql @q, N'@field varchar(32)', @field
  &EndErrorCount;
end else begin
  select @q = 'select top 1 @r = a.stt from @@table a, @@table b with (index(i)) where a.ma_px <> b.ma_px and a.ma_px like rtrim(b.ma_px) + ''%'''
  exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
  select top 1 @r = a.stt from @@table a, sfdmxuong b with (index(PK_sfdmxuong)) where a.ma_px like rtrim(b.ma_px) + '%'&Duplicate;
  select @q = 'select top 1 @r = b.stt from sfdmxuong a, @@table b with (index(i)) where a.ma_px like rtrim(b.ma_px) + ''%'''
  exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
end

&EndErrorMode;

-- Insert
&Inserting;
-- Update
if @type = '1' begin &Updating; end
