processing.text:

if @@admin = 0 and @type = '1' begin
  if @@sysDatabaseName.dbo.AI_ERP$System$GetAuthorize(@@admin, @@userID, 'Site', 'Edit') = 0 select @type = '0'
end

declare @message nvarchar(4000), @q nvarchar(4000), @duplicate nvarchar(4000), @irregular nvarchar(4000), @siteGroupConflict nvarchar(4000), @irregularChars varchar(128), @field varchar(32), @r int, @siteMethod varchar(1)

-- Site Group
select @siteMethod = rtrim(val) from options where name = 'm_gia_nh_kho'
select @siteMethod = isnull(@siteMethod, '0')

select @irregularChars = &IrregularValue;
select @irregular = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> có chứa các ký tự: ' + @irregularChars else 'The value of cell <span class="Highlight">%invalidCell</span> contains any of the following characters: ' + @irregularChars  end
select @duplicate = case @@language when 'v' then N'Giá trị tại ô <span class="Highlight">%invalidCell</span> đã có hoặc lồng nhau.' else 'The value of cell <span class="Highlight">%invalidCell</span> is invalid or already exists.' end
select @siteGroupConflict = case when @@language = 'v' then N'Nhóm kho tại ô <span class="Highlight">%invalidCell</span> đã khai báo trên đơn vị khác, không cập nhật được.' else N'Cannot update the site group <span class="Highlight">%invalidCell</span>. It has already been used by another unit.' end

create index i on @@table (ma_kho)

-- Delete
if @type = '0' delete @@table from @@table a join dmkho b on a.ma_kho = b.ma_kho

-- Checking
&Checking;
select @field = 'ma_kho'
if @$mode = 1 begin
  &IrregularMessage;
  &StartErrorCount;
  &InsertErrorTable; select @field, stt, @message from @@table where ma_kho like '%[' + @irregularChars + ']%'
  &EndErrorCount;
end else begin
  select @r = min(stt) from @@table where ma_kho like '%[' + @irregularChars + ']%'&Irregular;
end

select @field = 'ma_nh'
if @$mode = 1 begin
  &StartErrorCount;
  &InsertErrorTable; select @field, stt, 'C001' from @@table a join dmkho b with(nolock) on a.ma_nh <> '' and a.ma_nh = rtrim(b.ma_nh) and a.ma_dvcs <> b.ma_dvcs
  &EndErrorCount;
end else begin
  select @r = min(stt) from @@table a join dmkho b with(nolock) on a.ma_nh <> '' and a.ma_nh = rtrim(b.ma_nh) and a.ma_dvcs <> b.ma_dvcs&SiteGroupConflict;
end

-- Gather
select a.* into #k from @@table a join dmkho b on a.ma_kho = b.ma_kho
delete @@table where ma_kho in (select ma_kho from #k)
select identity(int, 1, 1) as stt, ma_dvcs, ma_kho, ten_kho, ten_kho2, dai_ly_yn, ma_nh, dia_chi&BI.Import.FieldPlant.Site; into #k1 from @@table

-- Checking
select @field = 'ma_kho'
if @$mode = 1 begin
  &StartErrorCount;
  select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a, @@table b with (index(i)) where a.ma_kho <> b.ma_kho and a.ma_kho like rtrim(b.ma_kho) + ''%''
  &w1;@field&w2;a.stt&w3;''00003''&w4; group by a.stt'
  exec sp_executesql @q, N'@field varchar(32)', @field
  &EndErrorCount;
  &StartErrorCount;
  select @q = '&InsertErrorTable; select @field, a.stt, ''00003'' from @@table a, dmkho b with (index(PK_dmkho)) where a.ma_kho like rtrim(b.ma_kho) + ''%''
  &w1;@field&w2;a.stt&w3;''00003''&w4; group by a.stt'
  exec sp_executesql @q, N'@field varchar(32)', @field
  &EndErrorCount;
  &StartErrorCount;
  select @q = '&InsertErrorTable; select @field, b.stt, ''00003'' from dmkho a, @@table b with (index(i)) where a.ma_kho like rtrim(b.ma_kho) + ''%''
  &w1;@field&w2;b.stt&w3;''00003''&w4; group by b.stt'
  exec sp_executesql @q, N'@field varchar(32)', @field
  &EndErrorCount;
end else begin
  select @q = 'select top 1 @r = a.stt from @@table a, @@table b with (index(i)) where a.ma_kho <> b.ma_kho and a.ma_kho like rtrim(b.ma_kho) + ''%'''
  exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
  select top 1 @r = a.stt from @@table a, dmkho b with (index(PK_dmkho)) where a.ma_kho like rtrim(b.ma_kho) + '%'&Duplicate;
  select @q = 'select top 1 @r = b.stt from dmkho a, @@table b with (index(i)) where a.ma_kho like rtrim(b.ma_kho) + ''%'''
  exec sp_executesql @q, N'@r int output', @r = @r output&Duplicate;
end

if @@admin = 0 begin
  if @type = '1' begin
    select @field = 'ma_dvcs'
    if @$mode = 1 begin
    &StartErrorCount;
    &InsertErrorTable; select @field, stt, '00002' from #k a join dmkho b on a.ma_kho = b.ma_kho where &Permissions;
    &w1;@field&w2;stt&w3;'00002'&w4;
    &EndErrorCount;
    end else begin
      select @r = min(a.stt) from #k a join dmkho b on a.ma_kho = b.ma_kho where &Permissions;
      &Error;
    end
  end
end

&EndErrorMode;

-- Insert
declare @max int
select @max = isnull(max(id), 0) from dmkho
&Inserting;

-- Update
if @type = '1' begin
  &ListWhenBeforeImportUpdateLog;
  &Updating;
end
